import React, { useState, useContext } from "react";
import UserContext from "../../contexts/UserContext";
import "./index.scss";
import { CardContent, Card, Typography, Grid, CardHeader } from "@mui/material";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { v4 as uuid } from "uuid";

const Semester = () => {
  const { courseList, semesterList } = useContext(UserContext);
  const flattenedSemesterList = [];
  semesterList.forEach((semester) => {
    semester.forEach((course) => {
      flattenedSemesterList.push(course);
    });
  });

  const itemsFromBackend = [];

  courseList.forEach((course) => {
    if (!flattenedSemesterList.includes(course)) {
      itemsFromBackend.push({ id: uuid(), content: course });
    }
  });

  const columnsFromBackend = {
    [uuid()]: {
      name: "Classes Taken",
      items: itemsFromBackend,
    },
    [uuid()]: {
      name: "Semester 1",
      items: [],
    },
    [uuid()]: {
      name: "Semester 2",
      items: [],
    },
    [uuid()]: {
      name: "Semester 3",
      items: [],
    },
    [uuid()]: {
      name: "Semester 4",
      items: [],
    },
  };

  const onDragEnd = (result, columns, setColumns) => {
    if (!result.destination) return;
    const { source, destination } = result;

    if (source.droppableId !== destination.droppableId) {
      const sourceColumn = columns[source.droppableId];
      const destColumn = columns[destination.droppableId];
      const sourceItems = [...sourceColumn.items];
      const destItems = [...destColumn.items];
      const [removed] = sourceItems.splice(source.index, 1);
      destItems.splice(destination.index, 0, removed);
      setColumns({
        ...columns,
        [source.droppableId]: {
          ...sourceColumn,
          items: sourceItems,
        },
        [destination.droppableId]: {
          ...destColumn,
          items: destItems,
        },
      });
    } else {
      const column = columns[source.droppableId];
      const copiedItems = [...column.items];
      const [removed] = copiedItems.splice(source.index, 1);
      copiedItems.splice(destination.index, 0, removed);
      setColumns({
        ...columns,
        [source.droppableId]: {
          ...column,
          items: copiedItems,
        },
      });
    }
  };
  const [columns, setColumns] = useState(columnsFromBackend);
  return (
    <div className="container semester">
      <DragDropContext
        onDragEnd={(result) => onDragEnd(result, columns, setColumns)}
      >
        {Object.entries(columns).map(([columnId, column], index) => {
          return (
            <Grid item xs={4}>
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                }}
                key={columnId}
              >
                <h2>{column.name}</h2>
                <div style={{ margin: 8 }}>
                  <Droppable droppableId={columnId} key={columnId}>
                    {(provided, snapshot) => {
                      return (
                        <div
                          {...provided.droppableProps}
                          ref={provided.innerRef}
                        >
                          <Card
                            sx={{
                              borderRadius: "16px",
                              boxShadow: "0 4px 30px rgba(0, 0, 0, 0.1)",
                              border: "1px solid rgba(255, 255, 255, 0.3)",
                              marginBottom: "10px",
                              background: snapshot.isDraggingOver
                                ? "lightblue"
                                : "lightgrey",
                              minHeight: 500,
                              width: 250,
                              paddign: 4,
                            }}
                          >
                            {column.items.map((item, index) => {
                              return (
                                <Draggable
                                  key={item.id}
                                  draggableId={item.id}
                                  index={index}
                                >
                                  {(provided, snapshot) => {
                                    return (
                                      <div
                                        ref={provided.innerRef}
                                        {...provided.draggableProps}
                                        {...provided.dragHandleProps}
                                      >
                                        <Card
                                          sx={{
                                            borderRadius: "16px",
                                            boxShadow:
                                              "0 4px 30px rgba(0, 0, 0, 0.1)",
                                            border:
                                              "1px solid rgba(255, 255, 255, 0.3)",
                                            margin: "10px 10px 10px 20px",
                                            width: "75%",
                                            padding: "10px",
                                            background:
                                              "rgba(255, 255, 255, 0.85)",
                                            backgroundColor: snapshot.isDragging
                                              ? "lightblue"
                                              : "rgba(255, 255, 255, 0.85)",
                                            color: "black",
                                          }}
                                        >
                                          <CardContent>
                                            {item.content}
                                          </CardContent>
                                        </Card>
                                      </div>
                                    );
                                  }}
                                </Draggable>
                              );
                            })}
                            {provided.placeholder}
                          </Card>
                        </div>
                      );
                    }}
                  </Droppable>
                </div>
              </div>
            </Grid>
          );
        })}
      </DragDropContext>
    </div>
  );
};

export default Semester;
