import React, { useState, useContext } from "react";
import UserContext from "../../contexts/UserContext";
import "./index.scss";
import { CardContent, Card, Grid, Box } from "@mui/material";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { v4 as uuid } from "uuid";

const Semester = () => {
  const { courseList, semesterList } = useContext(UserContext);
  const flattenedSemesterList = [];

  const itemsFromBackend = [];

  semesterList.forEach((semester) => {
    semester.forEach((course) => {
      flattenedSemesterList.push(course);
    });
  });

  courseList.forEach((course) => {
    if (!flattenedSemesterList.includes(course)) {
      itemsFromBackend.push({ id: uuid(), content: course });
    }
  });

  const columnsFromBackend = {};
  columnsFromBackend[[uuid()]] = {
    name: "Classes Taken",
    items: itemsFromBackend,
  };

  semesterList.forEach((semester, index) => {
    var courses = [];
    semester.forEach((course) => {
      courses.push({ id: uuid(), content: course });
    });
    var nameString = "Semester " + String(index + 1);
    columnsFromBackend[[uuid()]] = { name: nameString, items: courses };
  });
  console.log(columnsFromBackend);

  const onDragEnd = (result, columns, setColumns) => {
    if (!result.destination) return;
    const { source, destination } = result;

    if (source.droppableId !== destination.droppableId) {
      const sourceColumn = columns[source.droppableId];
      const destColumn = columns[destination.droppableId];
      const sourceItems = [...sourceColumn.items];
      const destItems = [...destColumn.items];
      const [removed] = sourceItems.splice(source.index, 1);
      destItems.splice(destination.index, 0, removed);
      setColumns({
        ...columns,
        [source.droppableId]: {
          ...sourceColumn,
          items: sourceItems,
        },
        [destination.droppableId]: {
          ...destColumn,
          items: destItems,
        },
      });
    } else {
      const column = columns[source.droppableId];
      const copiedItems = [...column.items];
      const [removed] = copiedItems.splice(source.index, 1);
      copiedItems.splice(destination.index, 0, removed);
      setColumns({
        ...columns,
        [source.droppableId]: {
          ...column,
          items: copiedItems,
        },
      });
    }
  };
  const [columns, setColumns] = useState(columnsFromBackend);
  return (
    <div className="container semester">
      <DragDropContext
        onDragEnd={(result) => onDragEnd(result, columns, setColumns)}
      >
        <Grid container columns={{ xs: 4, sm: 8, md: 16 }}>
          {Object.entries(columns).map(([columnId, column], index) => {
            if (index === 0) {
              return (
                <div>
                  <h2>{column.name}</h2>
                  <div style={{ margin: 8 }}>
                    <Droppable droppableId={columnId} key={columnId}>
                      {(provided, snapshot) => {
                        return (
                          <div
                            {...provided.droppableProps}
                            ref={provided.innerRef}
                          >
                            <Card
                              className="droppable"
                              sx={{
                                background: snapshot.isDraggingOver
                                  ? "lightblue"
                                  : "lightgrey",
                              }}
                            >
                              {column.items.map((item, index) => {
                                return (
                                  <Draggable
                                    key={item.id}
                                    draggableId={item.id}
                                    index={index}
                                  >
                                    {(provided, snapshot) => {
                                      return (
                                        <div
                                          ref={provided.innerRef}
                                          {...provided.draggableProps}
                                          {...provided.dragHandleProps}
                                        >
                                          <Card className="draggable">
                                            <CardContent>
                                              {item.content}
                                            </CardContent>
                                          </Card>
                                        </div>
                                      );
                                    }}
                                  </Draggable>
                                );
                              })}
                              {provided.placeholder}
                            </Card>
                          </div>
                        );
                      }}
                    </Droppable>
                  </div>
                </div>
              );
            }
            return (
              <Grid item xs={4} sm={4} md={4} key={index}>
                <div
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                  }}
                  key={columnId}
                >
                  <h2>{column.name}</h2>
                  <div style={{ margin: 8 }}>
                    <Droppable droppableId={columnId} key={columnId}>
                      {(provided, snapshot) => {
                        return (
                          <div
                            {...provided.droppableProps}
                            ref={provided.innerRef}
                          >
                            <Card
                              className="droppable"
                              sx={{
                                background: snapshot.isDraggingOver
                                  ? "lightblue"
                                  : "lightgrey",
                              }}
                            >
                              {column.items.map((item, index) => {
                                return (
                                  <Draggable
                                    key={item.id}
                                    draggableId={item.id}
                                    index={index}
                                  >
                                    {(provided, snapshot) => {
                                      return (
                                        <div
                                          ref={provided.innerRef}
                                          {...provided.draggableProps}
                                          {...provided.dragHandleProps}
                                        >
                                          <Card className="draggable">
                                            <CardContent>
                                              {item.content}
                                            </CardContent>
                                          </Card>
                                        </div>
                                      );
                                    }}
                                  </Draggable>
                                );
                              })}
                              {provided.placeholder}
                            </Card>
                          </div>
                        );
                      }}
                    </Droppable>
                  </div>
                </div>
              </Grid>
            );
          })}
        </Grid>
      </DragDropContext>
    </div>
  );
};

export default Semester;
